script:
inputs:
  text/page:
    type: number
    default: 0
  text/limit:
    type: number
    default: 30
  text/filter:
    type: freeform

start:
  data.query:
    output: results
    inputs:
      query@text:
        type: record.types
        {% if inputs.filter -%}
        filter: {{inputs.filter}}
        {% endif -%}
        {% if inputs.limit -%}
        limit: {{inputs.limit}}
        {% endif -%}
        {% if inputs.page -%}
        page: {{inputs.page}}
        {% endif -%}
        format: dictionaries
    on_success:
      return:
        data@key: results:data
        paging@key: results:_:paging
    on_error:
      error:
        message@key: results:error
